import { FILTER, ORDER } from "./types";



const initialState = {
  myFavorites: [],
  allCharacters: [],
};

export default function reducers(state = initialState, { type, payload }) {
  switch (type) {
    case 'ADD_FAV':
      return { ...state, myFavorites: payload, allCharacters: [...state.allCharacters, ...payload] };

    case 'REMOVE_FAV':
      return { ...state, myFavorites: payload };

    case FILTER:
      if (payload === 'all') {
        return {
          ...state,
          myFavorites: state.allCharacters,
        };
      }

      const filteredCharacters = state.allCharacters.filter(character => character.gender === payload);
      return {
        ...state,
        myFavorites: filteredCharacters,
      };

    case ORDER:
      const orderedCharacters = [...state.allCharacters].sort((a, b) => {
        if (payload === 'A') {
          return a.id - b.id;
        } else if (payload === 'D') {
          return b.id - a.id;
        }
        return 0;
      });
      return {
        ...state,
        myFavorites: orderedCharacters,
      };

    default:
      return state;
  }
}
