export default function validation(input) {
  const error = {};

  const regexEmail = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
  const regexPassword = new RegExp("[0-9]");

  if (!regexEmail.test(input.email)) {
    error.email = "Debe ingresar un correo electrónico válido";
  }
  if (!input.email) {
    error.email = "Debe ingresar un correo electrónico";
  }
  if (input.email.length > 35) {
    error.email = "El correo electrónico no debe tener más de 35 caracteres";
  }
  if (!regexPassword.test(input.password)) {
    error.password = "La contraseña debe contener al menos un número";
  }
  if (input.password.length < 6 || input.password.length > 10) {
    error.password = "La contraseña debe tener entre 6 y 10 caracteres";
  }
  return error;
}
