// import characters from "./data.js";
import axios from "axios";
import { useEffect, useState } from "react";
import { Route, Routes } from "react-router-dom";
import Cards from "./components/CardsComponets/Cards.jsx";
import Nav from "./components/CardsComponets/Nav";
import About from "./components/CardsComponets/About";
import Deatil from "./components/CardsComponets/Deatil";
import "./App.css";

function App() {
  const [characters, setCharacters] = useState([]);

  const onSearch = (id) => {
    axios(`https://rickandmortyapi.com/api/character/${id}`).then(
      ({ data }) => {
        if (data.name) {
          setCharacters((oldChars) => [...oldChars, data]);
        } else {
          window.alert("¡No hay personajes con este ID!");
        }
      }
    );
  };

  const onClose = (id) => {
    setCharacters(characters.filter((caracter) => caracter.id !== Number(id)));
  };

  return (
    <div className="App">
      <Nav onSearch={onSearch} />
      <Routes>
        <Route path="/About" element={<About />} />
        <Route path="/detail/:id" element={<Deatil />} />
        <Route
          path="/home"
          element={<Cards characters={characters} onClose={onClose} />}
        />
      </Routes>
    </div>
  );
}

export default App;
