//                car
//             }
//          </Routes>
//          <Nav onSearch={onSearch}/>
//          <Cards characters={characters} onClose={onClose} />
//       </div>
//    );
// }
// export default App;

return (
    <div className='App'>
      <Router>
        <Routes>
          <Route path='/home' element={Card} />
        </Routes>
      </Router>
      <Nav onSearch={onSearch} />
      <Cards characters={characters} onClose={onClose} />
    </div>
  );
}

export default App;
