const initialState = {
  myFavorites: [],
  allCharacters: [],
};

export default function reducers(state = initialState, { type, payload }) {
  switch (type) {
    case 'ADD_FAV':
      return {
        ...state,
        myFavorites: [...state.myFavorites, payload],
      };
    case 'REMOVE_FAV':
      const filteredFav = state.myFavorites.filter(fav => fav.id !== Number(payload));
      return {
        ...state,
        myFavorites: filteredFav,
      };
    case 'FILTER':
      const filteredChars = state.allCharacters.filter(char => char.gender === payload);
      return {
        ...state,
        myFavorites: filteredChars,
      };
    case 'ORDER':
      const orderedChars = [...state.allCharacters].sort((a, b) => {
        if (payload === 'A') {
          return a.id - b.id;
        } else if (payload === 'D') {
          return b.id - a.id;
        }
        return 0;
      });
      return {
        ...state,
        myFavorites: orderedChars,
      };
    default:
      return state;
  }
}
