import { useState } from 'react';
import './App.css';
import Cards from './components/cards/Cards.jsx';
import Nav from './components/nav/Nav';
import axios from 'axios';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Card from './components/card/Card';
import Detail from './components/detail/Detail'; // import the Detail component

function App() {
  const [characters, setCharacters] = useState([]);

  const example = {
    id: 1,
    name: 'Rick Sanchez',
    status: 'Alive',
    species: 'Human',
    gender: 'Male',
    origin: {
      name: 'Earth (C-137)',
      url: 'https://rickandmortyapi.com/api/location/1',
    },
    image: 'https://rickandmortyapi.com/api/character/avatar/1.jpeg',
  };

  const onSearch = (id) => {
    axios(`https://rickandmortyapi.com/api/character/${id}`).then(
      ({ data }) => {
        if (data.name) {
          setCharacters((oldChars) => [...oldChars, data]);
        } else {
          window.alert('¡No hay personajes con este ID!');
        }
      }
    );
  };

  const onClose = (id) => {
    setCharacters((characters) =>
      characters.filter((caracter) => caracter.id !== Number(id))
    );
  };

  return (
    <div className='App'>
      <Router>
        <Nav onSearch={onSearch} />
        <hr />
        <Routes>
          <Route path='/home' element={<Cards characters={characters} onClose={onClose} />} />
          <Route path='/about' element={<div>About page</div>} />
          <Route path='/detail/:id' element={<Detail />} />
        </Routes>
      </Router>
    </div>
  );
}

export default App;

// Ahora crearemos las rutas de los componentes. Cada componente debe renderizarse en las siguientes rutas:

// Nav debe que aparecer en todas las rutas.
// Cards debe aparecer solo en la ruta /home.
// About debe aparecer solo en la ruta /about.
// Detail debe aparecer solo en la ruta /detail/:id.
// [NOTA]: ten en cuenta que la ruta del componente Detail recibe un parámetro id.

